// MS
declare namespace parseMilliseconds {
	interface Parsed {
		days: number;
		hours: number;
		minutes: number;
		seconds: number;
		milliseconds: number;
		microseconds: number;
		nanoseconds: number;
	}
}

declare function parseMilliseconds(
	milliseconds: number
): parseMilliseconds.Parsed;

export = parseMilliseconds;